(function(a){jQuery.fn.moClear=function(b){defaults={btnClass:"moClear_btn"};b=a.extend(defaults,b);return this.each(function(b,c){var d=a(this),e='<a style="display:none;position:absolute;top:8px;right:8px;" class="'+defaults.btnClass+'"></a>',f=a(this).attr("placeholder");d.wrap('<div style="display:inline-block;position:relative;"></div>');d.after(e);d.focus(function(){if(d.val()!=""){d.parent().find("."+defaults.btnClass).fadeIn(150)}});d.blur(function(){if(d.val()!=""){d.parent().find("."+defaults.btnClass).fadeOut(150)}});d.keyup(function(){if(d.val()!=""){d.parent().find("."+defaults.btnClass).fadeIn(150)}else if(d.val()===""){d.parent().find("."+defaults.btnClass).fadeOut(150)}});d.parent().find("."+defaults.btnClass).click(function(){a(this).hide();d.val("");d.focus()})})}})(jQuery)